## Find sqrt(sum of 1/k^2.1), for k = 1, 2, 3, ....n = 100000x = rep(1,n)for (k in 2:n){    x[k] = x[k-1] + 1/k^2.1}y = sqrt(x)par(mfrow=c(1,2)) ## do this if you want two plots per page.plot(c(0,n),c(min(y),max(y)),type="n",xlab="k",     ylab="sqrt{sum of 1/i^2.1 from i=1 to k}")points(1:n,y,pch=".")## problems: the range of the y-axis is too big, ## and the labels on the axes are ugly.max(y) ## 1.249085plot(c(0,n),c(1.248,1.2491),type="n",xlab="k",     ylab="sqrt{sum of 1/i^2.1 from i=1 to k}")points(1:n,y,pch=".")## didn't help enough. You have to zoom in a lot more here on the y-axis.plot(c(0,n),c(1.24905,1.249086),type="n",xlab="k",     ylab="sqrt{sum of 1/i^2.1 from i=1 to k}")points(1:n,y,pch=".")## problem: the labels on the axes are still ugly.plot(c(0,n),c(1.24905,1.249086),type="n",xlab="k",     ylab=expression(sqrt(sum(frac(1,i^2.1),i==1, k))))points(1:n,y,pch=".")abline(h=1.249085,lty=2)## problems: the labels on both axes are still not great.plot(c(0,n/1000),c(1.24905,1.249086),type="n",     xlab="k (thousands)",ylab="",cex.axis=.7)    mtext(s=2,l=1.65, at = 1.249065,       expression(sqrt(sum(frac(1,i^2.1),i==1, k))))points(c(1:n)/1000,y,pch=".")abline(h=1.249085,lty=2)par(mfrow=c(1,1)) ## just one plot on the pageplot(c(0,n/1000),c(1.24905,1.249086),type="n",xlab="k (thousands)",ylab="",cex.axis=.7)    mtext(s=2,l=1.65, at = 1.249065, expression(sqrt(sum(frac(1,i^2.1),i==1, k))))points(c(1:n)/1000,y,pch=".")abline(h=1.249085,lty=2)